INSERT INTO self_module VALUES('B1','about','2012-06-04','2099-12-31','<table border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"480\">\r\n    <tbody>\r\n        <tr>\r\n            <td><img alt=\"\" width=\"480\" height=\"38\" src=\"/df19/hyfiles/about-a.jpg\" /></td>\r\n        </tr>\r\n        <tr>\r\n            <td class=\"uk1\" background=\"/df19/hyfiles/about-b.jpg\" style=\"padding-left: 15px; padding-right: 15px\">\r\n            <div><font color=\"#000080\">豪聯國際創立於1987年，以專業顯示器供應商的良好形象長久於電腦資訊業界。先後代理國內、外知名顯示器以及製造生產內外銷專業顯示器；並OEM生產，供應給各PC大廠與標案市場等。公司成立的目的是要永續經營，在顯示器相關產品上開發特用顯示器，並提升產品性能、價值以及服務的品質，而這樣的經營理念也促使整個企業體能夠穩定地成長。27年來，豪聯國際已架構出完善的全省行銷通路與專業的維修物流系統，1995年起，豪聯國際成立了&quot;螢幕孤兒之家&quot;，為有螢幕孤兒困擾的經銷商及消費者提供一個舒適、安全、有保障的家園。</font></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><img alt=\"\" width=\"480\" height=\"47\" src=\"/df19/hyfiles/about-c.jpg\" /></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','1','0','3','2012-06-04 21:49:06','3','2013-01-30 23:21:53','1');
